mapper.select:
select bno, title, content, writer, regdate, viewcnt
from tbl_board
where bno = #{bno}


select bno, title, content, writer, regdate, viewcnt
from tbl_board
where bno > 0
and post_category_idx = #{idx}
order by bno desc, regdate desc
limit #{pageStart}, #{perPageNum}

select * from tbl_board where bno > 0 and post_category_idx = #{idx}
and title like CONCAT('%', #{keyword}, '%') and content like CONCAT('%', #{keyword}, '%') and writer like CONCAT('%', #{keyword}, '%') and (title like CONCAT('%', #{keyword}, '%') OR content like CONCAT('%', #{keyword}, '%')) and (content like CONCAT('%', #{keyword}, '%') OR writer like CONCAT('%', #{keyword}, '%')) and (title like CONCAT('%', #{keyword}, '%') OR content like CONCAT('%', #{keyword}, '%') OR writer like CONCAT('%', #{keyword}, '%'))
order by bno desc limit #{pageStart}, #{perPageNum}
